    public int[] findRedundantDirectedConnection(int[][] edges) {
        // int root = findRoot(edges);

        int[] parent = new int[edges.length + 1];
        int node1 = -1;
        int node2 = -1;
        int node3 = -1;

        for(int[] edge : edges){
            int root1 = find(parent, edge[0]);
            int root2 = find(parent, edge[1]);

            if(parent[edge[1]] == edge[1] && root1 != root2){
                // normal edge
                // add this edge
                parent[edge[1]] = edge[0];
            }else if(root1 == root2){
                // cycle
                // record the current edge that create a cycle, and remove the edge
								// no more cycle
                node2 = edge[0];
