                if(node2 != -1){
                    parent[node3] = node2;
                }

                // record two conflict edges
                node1 = parent[edge[1]];
                node2 = edge[0];
                node3 = edge[1];

                // remove the two edges, then no cycle anymore
                parent[node3] = node3;
            }
        }

        // if no conflict edge or if the first edge is not in cycle.
        return node1 == -1 || find(parent, node1) != node3 ? new int[]{node2, node3} : new int[]{node1, node3};    
