        for (int i=0;i<n;i++) curEdge = edges[parantEdgePointer[curEdge]][0]; // moving curEdge to become his parent n times
        //now just itarate once again and mark the most right edge in the cycle.
        for (int i=0;i<n;i++) {
            curEdge = edges[parantEdgePointer[curEdge]][0];
            mostRight = Math.max(mostRight,parantEdgePointer[curEdge]);
        }
        return edges[mostRight];
    }
}
