class Solution {





    public int[] findRedundantDirectedConnection(int[][] edges) {
        //check double parent
        int ans1 = -1;
        int ans2 = -1;
        int [] inDegree = new int[edges.length+1];
        Arrays.fill(inDegree,-1);

        for( int i = 0 ; i < edges.length; i++ ){
            int var1 = edges[i][0];
            int var2 = edges[i][1];
            if( inDegree[var2] == -1 ) inDegree[var2] = i;
            else {
                ans1 = inDegree[var2];
                ans2 = i;    //store the double parent
            }
        }


        //(u,v)  u is parent  u points to v
        UnionFind uf = new UnionFind(edges.length*2);
        for(int [] edge: edges){
            if(ans2!=-1 && edge == edges[ans2]) continue;
            int u = edge[0];
            int v = edge[1];
            int paru = uf.find(u);
            int parv = uf.find(v);
            if(paru == parv) return ans1==-1 ? edge: edges[ans1];
            uf.union(u,v);
        }
        return edges[ans2];
    }



    private class UnionFind{
        int parent[];
        public UnionFind(int n){
            parent = new int[n];
            for( int i = 0 ; i < n; i++ ){
                parent[i] = i;
            }
        }

        public int find( int a ){
            int parA = parent[a];
            if(parA == a) return a;
            parent[a] = find(parent[a]);
            return parent[a];
        }

        public void union(int a, int b){
            int parA = find(a);
            int parB = find(b);
            if(parA == parB) return;
            parent[parB] = parA;
        }
    }
}

