var findRedundantDirectedConnection = function (edges = [[0]]) {

	class Vertices {
        constructor(vertex = 0) {
            this.vertex = vertex;
            this.d = 0;
            this.f = 0;
            this.color = 'WHITE';
            this.parent = null;
            this.edgeIndex = [];
        }
    }

    class Graph {
        constructor() {
            this.vertices = [];
            this.Adj = [];
        }
    }

    let time = 0;
    let edgeIndex = -1;
    let vertices = [];
    let verticesIndex = 0;
    let skipRedundantNodeSeaarch = true;

    var DFS_VISIT = function (G = new Graph(), u = new Vertices()) {
        time++;
        u.d = time;
        u.color = 'GRAY';
        for (let i = 0; i < G.Adj[u.vertex].length; i++) {
            let v = G.Adj[u.vertex][i];
            if (v.color === 'WHITE') {
                v.parent = u;
                DFS_VISIT(G, v);
            }
            else if (!skipRedundantNodeSeaarch) {
                if (v.color === 'GRAY') { //back edge
                    edgeIndex = u.edgeIndex[v.vertex];
                    if (v.parent === null) {
                        let newParent = u.parent;
                        if (newParent.vertex === v.vertex) { //cycle
                            if (newParent.edgeIndex[u.vertex] > edgeIndex) edgeIndex = newParent.edgeIndex[u.vertex];
                        }
                        else {
                            let newV = u;
                            while (newParent && newParent.vertex !== v.vertex) {
                                if (newParent.edgeIndex[newV.vertex] > edgeIndex) edgeIndex = newParent.edgeIndex[newV.vertex];
                                newParent = newParent.parent;
                                newV = newV.parent;
                            }
                        }
                    }
                }
                else {
                    if(v.parent !== null) {
                        let newParent = v.parent;
                        edgeIndex = u.edgeIndex[v.vertex];
                        let aliasEdgeIndex = newParent.edgeIndex[v.vertex];
                        if (aliasEdgeIndex > edgeIndex) edgeIndex = aliasEdgeIndex;
                    }
                }
            }
        }
        u.color = 'BLACK';
        time++;
        u.f = time;
        if (skipRedundantNodeSeaarch) vertices[vertices.length - 1 - verticesIndex++] = u;

    }

    var DFS = function (G = new Graph()) {
        for (let i = 0; i < G.vertices.length; i++) {
            if (G.vertices[i].color === 'WHITE') {
                DFS_VISIT(G, G.vertices[i]);
            }
        }
    }

    let G = new Graph();

    for (let i = 0; i < edges.length; i++) {
        if (!G.vertices[edges[i][0] - 1]) G.vertices[edges[i][0] - 1] = new Vertices(edges[i][0] - 1);
        if (!G.vertices[edges[i][1] - 1]) G.vertices[edges[i][1] - 1] = new Vertices(edges[i][1] - 1);

        G.vertices[edges[i][0] - 1].edgeIndex[edges[i][1] - 1] = i;

        if (!G.Adj[edges[i][0] - 1]) G.Adj[edges[i][0] - 1] = [];
        if (!G.Adj[edges[i][1] - 1]) G.Adj[edges[i][1] - 1] = [];
        G.Adj[edges[i][0] - 1].push(G.vertices[edges[i][1] - 1]);
    }

    vertices.length = G.vertices.length;
    DFS(G);
    for (let i = 0; i < vertices.length; i++) {
        vertices[i].d = 0;
        vertices[i].f = 0;
        vertices[i].color = 'WHITE';
        vertices[i].parent = null;
    }
    G.vertices = vertices;
    skipRedundantNodeSeaarch = false;
    DFS(G);
    return edges[edgeIndex];

}

