            // graph.
            List<Integer> tmp = graph.getOrDefault(e[0], 
                                    new ArrayList<>());
            tmp.add(e[1]);
            graph.put(e[0], tmp);

            // Parent status.
            tmp = parent.getOrDefault(e[1], 
                        new ArrayList<>());
            tmp.add(e[0]);
            if (tmp.size() > 1) oddNode = e[1];
            parent.put(e[1], tmp);

            // edge Index.
            Map<Integer, Integer> tmp2 =
                edgeIndex.getOrDefault(e[0], new HashMap<>());
            tmp2.put(e[1], i);
            edgeIndex.put(e[0], tmp2);
        }

        List<Integer> cycle = new ArrayList<>();
        int n = 1001;
        int[] stat = new int[n];
        for (int i = 0; i < n; i++) {
            if (stat[i] != 0) continue;

            dfs(i, graph, stat, new ArrayList<>(), cycle);

            if (cycle.size() == 0) {
                continue;
            } else {
                // found cycle.
                int maxEdgeInd = 0;
                int twinInEdgeNode = -1;
                int cLen = cycle.size();
                Set<Integer> cycleSet = new HashSet<>();
                for (int c = 0; c < cLen; c++) {
                    int s = cycle.get(c);
                    int t = cycle.get((c + 1) % cLen);

                    // Cycle did not pass root.
                    if (parent.get(s).size() == 2) {
                        twinInEdgeNode = s;
                    }

                    cycleSet.add(s);
                    maxEdgeInd = Math.max(maxEdgeInd, 
                                edgeIndex.get(s).get(t));
                }

                // Cycle pass root, return maxInd edge.
                if (twinInEdgeNode == -1) return edges[maxEdgeInd];

                // Cycle did not pass root.
                int a = parent.get(twinInEdgeNode).get(0);
                int b = parent.get(twinInEdgeNode).get(1);
                int ai = edgeIndex.get(a).get(twinInEdgeNode);
                int bi = edgeIndex.get(b).get(twinInEdgeNode);

                return cycleSet.contains(a) ? edges[ai] : edges[bi];
            }
        }

        // visited some black node twice.
        int a = parent.get(oddNode).get(0);
        int b = parent.get(oddNode).get(1);
        int ai = edgeIndex.get(a).get(oddNode);
        int bi = edgeIndex.get(b).get(oddNode);

        return ai > bi ? edges[ai] : edges[bi];
    }

    public void dfs(int cur, 
                    Map<Integer, List<Integer>> graph,
                    int[] stat, List<Integer> tracker, 
                    List<Integer> cycle) {
        if (cycle.size() > 0) return;

        if (stat[cur] == 1) {
            // found cycle.
            int i = 0;
            while (tracker.get(i) != cur) i++;

            for (int j = i; j < tracker.size(); j++) {
                cycle.add(tracker.get(j));
            }

            return;
        }

        if (stat[cur] == 2) {
            return;
        }

        stat[cur] = 1;
        tracker.add(cur);

        if (graph.containsKey(cur)) {
            for (int n : graph.get(cur)) {
                dfs(n, graph, stat, tracker, cycle);
            }    
        }

        stat[cur] = 2;
        tracker.remove(tracker.size() - 1);
    }
}
