class Solution {
public:
	//
	vector<int> parent;

	int findParent(int u) {
		return parent[u] = (parent[u] == u ? u : findParent(parent[u]));
	}


	void _union(int u, int v) {

		int pv = findParent(v); int pu = findParent(u);
		parent[pv] = pu;
	}


	vector<int> findRedundantDirectedConnection(vector<vector<int>>& edges) {
		parent.resize(1002);
		for (int i = 0; i < 1002; i++) parent[i] = i;
		vector<int> pans;
		vector<int> ans;
		vector<int> ind(1002, -1);
		int bl1 = -1, bl2 = -1;

		for (int i = 0; i < edges.size(); i++) {
			int u = edges[i][0];
			int v = edges[i][1];

			if (ind[v] == -1) ind[v] = i;

			else if (ind[v] != -1) {
				bl2 = ind[v];
				bl1 = i;
				ind[v] = i;
			}

			if (findParent(u) == findParent(v)) {
				// cout<<"parent of "<<u<<" is equal to parent of  "<<v<<" which is equal to "<<findParent(u) <<"\n";
				pans = edges[i];
			} else if (findParent(u) != findParent(v)) {
				_union(u, v);
			}

		}
		// cout<<bl1<<bl2<<'\n';
		// cout<<pans.empty()<<'\n';
		// for(int x : pans)cout<<x<<" ";
		// if there are no nodes with 2 parents (means bl1 and bl2 are -1 ) then it means that we have to detect just the cycle
		if (bl1 == -1 && bl2 == -1) {
			// there are no nodes with 2 parents
			ans = pans;
			return ans;

		} else if (pans.empty()) {
			// means there are no cycles (we simply use the method in redundant connections 1)
			ans = edges[bl1];
			return ans;
		} else {
			// in this case there are 2 parents and a cycle (so the answer can be any of bl1 and bl2)
			// bl1 is the latest edge which is pointing to the node which has two parents
			// bl2 was the first edge (according to what is given in the problem ) which is pointing to thee node having two parents
			parent.resize(1002);
			for (int i = 0; i < 1002; i++) {
				parent[i] = i;
			}

			// just expel bl1 and check
			for (int i = 0; i < edges.size(); i++) {
				if (i != bl1) {
					int u = edges[i][0];
					int v = edges[i][1];

					if (findParent(u) == findParent(v)) {
						ans = edges[bl2];
						return ans;
					}

					else {
						_union(u, v);
					}
				}
			}

			ans = edges[bl1];
			return ans;

		}

	}
};


