class Solution {
    int rank_[1007],par[1007];
    int find(int x)
    {
        if(par[x]==x)return x;
        return par[x]=find(par[x]);
    }
    bool union_(int x,int y)
    {
        int a = find(x);
        int b = find(y);
        if(a==b)return true;
        if(rank_[a]>rank_[b])par[b]=a;
        else if(rank_[b]>rank_[a])par[a]=b;
        else
        {
            rank_[a]++; par[b]=a;
        }
        return false;
    }
public:
    vector<int> findRedundantDirectedConnection(vector<vector<int>>& edges) {
        int n = edges.size(),x,y;
        for(int i=1;i<=n;i++)par[i]=0;
        vector<int> A,B;
        for(int i=0;i<n;i++)
        {
            x = edges[i][0];
            y = edges[i][1];
            if(par[y])
            {
                A = {par[y],y};
                B = {x,y};
                edges[i][1] = 0;
            }
            else
                par[y]=x;
        }
        for(int i=1;i<=n;i++)
        { par[i]=i; rank_[i]=1;}
        for(int i=0;i<n;i++)
        {
            x = edges[i][0];
            y = edges[i][1];
            if(y == 0)continue;
            if(union_(x,y))
            {
                if(A.size()==0)
                   A = {x,y};
                return A;
            }
        }
        return B;
    }
};

