/*Steps----
     1.) Find the node having two parents(If there then two invalid possibilities(let A & B) and makes B invalid)
     2.) Use DSU to find cycle.
          ---->If no cycle found then return B
          ---->If cycle found and no node with two parents the return current edge
          ---->else return A

*/
class DSU   //NORMAL DSU
{
    public:
    vector<int>parent;
    DSU(int n)
    {
        parent.resize(n);
        for(int i=0;i<n;i++)
            parent[i]=i;
    }
    int Find(int x)
    {
        if(x!=parent[x])
            parent[x]=Find(parent[x]);
        return parent[x];
    }
    void Union(int a,int b)
    {
        parent[b]=a;
    }
};
class Solution {
public:
    vector<int> findRedundantDirectedConnection(vector<vector<int>>& edges) 
    {
        int n=edges.size();

        //Step-1

        vector<int>A,B;
        vector<int>parent(n+1,0); //To find node with two parents
        for(auto &edge:edges)
        {
            if(parent[edge[1]]==0)
                parent[edge[1]]=edge[0];
            else
            {
                A={parent[edge[1]],edge[1]};
                B=edge;                     
                //Two possibilities A & B
                edge[1]=0; //Make B invalid;
            }
        }

        //Step 2 DSU

        DSU dsu=DSU(n+1);
        for(auto v:edges)
        {
            int a=v[0],b=v[1];
            if(b==0)
                continue;
            int pa=dsu.Find(a),pb=dsu.Find(b);
            if(pa==pb)  //Cycle found
            {
                if(A.empty())   //If no node with two parents return current node
                    return v;
                return A;       //Else return A
            }
            else
                dsu.Union(pa,pb);
        }
        return B;  //No cycle found return B
    }
};
