class UnionFind {
   int root[];
   int height[];

	public UnionFind(int n) {
		root = new int[n];
		height = new int[n];
		for (int i = 0; i < n; i++) {
			root[i] = i;
		}
	}

	public void union(int x, int y) {
		int rootX = find(x), rootY = find(y);
		if (height[rootX] < height[rootY]) {
			root[rootX] = rootY;
		}
		else if (height[rootY] < height[rootX]) {
			root[rootY] = rootX;
		}
		else {
			root[rootX] = rootY;
			height[rootY]++;
		}
	}

	public int find(int x) {
		return root[x] == x ? x : find(root[x]);
	}
}

