class Solution {
    public int[] findRedundantDirectedConnection(int[][] edges) {
        final int vertexCount   = edges.length;
        final int[] parents     = new int[vertexCount + 1];
        int[] possibleRedundantEdge = null;
        for (int i = 0; i < vertexCount; ++i) {
            final int[] edge    = edges[i];
            final int from      = edge[0];
            final int to        = edge[1];
            if (parents[to] != 0) {
                possibleRedundantEdge = edge;
                continue;
            }
            parents[to] = from;
        }
        if (possibleRedundantEdge != null) {
            final int crossVertex = possibleRedundantEdge[1];
            for (int p = parents[crossVertex]; ; ) {
                final int grandParent = parents[p];
                if (grandParent == 0)           return possibleRedundantEdge;
                if (grandParent == crossVertex) return new int[]{parents[crossVertex], crossVertex};
                p = grandParent;
            }
        }
        final int[] ancestors = new int[vertexCount + 1];
        for (int i = 1; i <= vertexCount; ++i) {
            ancestors[i] = i;
        }
        final int[] weights = new int[vertexCount + 1];
        for (int i = 1; i <= vertexCount; ++i) {
            weights[i] = 1;
        }
        for (int i = 0; i < vertexCount; ++i) {
            final int[] edge    = edges[i];
            final int from      = edge[0];
            final int to        = edge[1];
            final int fromRoot  = getRoot(ancestors, from);
            final int toRoot    = getRoot(ancestors, to);
            if (fromRoot == toRoot) return edge;
            final int fromWeight    = weights[fromRoot];
            final int toWeight      = weights[toRoot];
            if (fromWeight < toWeight) {
                ancestors[fromRoot] = toRoot;
                weights[toRoot]     += fromWeight;
            } else {
                ancestors[toRoot] = fromRoot;
                weights[fromRoot] += toWeight;
            }
        }
        return null;
    }

    private int getRoot(int[] ancestors, int vertex) {
        int p = ancestors[vertex];
        while (true) {
            final int grandparent = ancestors[p];
            if (p == grandparent) return p;
            ancestors[p]    = grandparent;
            p               = grandparent;
        }
    }
}
