class Solution {
    struct UnionFind {
        UnionFind(int start_index, int size)
          : root_(size, -1),
            size_(size, 0),
            start_index_(start_index),
            count_(0)
        {
            for (int i = start_index_; i < root_.size(); i++) {
                root_[ i ] = i;
                count_++;
            }        
        }

        int findRoot(int node) {
            while (root_[ node ] != node) {
                root_[node] = root_[root_[node]];
                node = root_[node];
            }
            return node;
        }

        bool join(int node1, int node2) {
            int root1 = findRoot(node1);
            int root2 = findRoot(node2);
            if (root1 == root2) {
                return false;
            }

            if (size_[root1] > size_[root2]) {
                size_[root1] += size_[root2];
                root_[root2] = root1;
            } else {
                size_[root2] += size_[root1];
                root_[root1] = root2;
            }
            count_--;
            return true;
        }

        int size() const {
            return count_;
        }

    private:
        std::vector<int> root_;
        std::vector<int> size_;
        int start_index_;
        int count_;
    };

public:
    std::vector<int> findRedundantDirectedConnection(const std::vector<std::vector<int>>& edges) {
        std::unordered_map<int, std::vector<int>> parents;
        for (const std::vector<int> & edge : edges) {
            int src = edge.front();
            int dst = edge.back();
            parents[dst].push_back(src);

            if (parents[dst].size() == 2) {
                // looking for the last occurrence in edges (backward) 
                for (int p = parents[dst].size() - 1; p >= 0; p--) {
                    std::vector<int> redundant{ parents[dst][p], dst };
                    if (isValidWithout(edges, redundant)) {
                        return redundant;
                    }
                }
            }
        }

        // We have a cycle, union all nodes
        UnionFind dsu(1, edges.size() + 1);
        for (const std::vector<int> & edge : edges) {
            if (!dsu.join(edge.front(), edge.back())) {
                return edge; // the last edge in cycle (already connected)
            }
        }
        return {};
    }

private:
    bool isValidWithout(const std::vector<std::vector<int>>& edges, const std::vector<int>& redundant) {
        UnionFind dsu(1, edges.size() + 1);
        for (const std::vector<int> & edge : edges) {
            if (edge == redundant) {
                continue;
            }
            dsu.join(edge.front(), edge.back());
        }
        return dsu.size() == 1;
    }
};
