class UnionFind {
    int[] parent;
    int[] rank;

    public UnionFind (int size) {
        parent = new int[size + 1];
        rank = new int[size + 1];
        for (int i = 0; i <= size; ++i) {
            parent[i] = i;
        }
    }

    public int find(int u) {
        if (parent[u] != u) {
            parent[u] = find(parent[u]);
        }
        return parent[u];
    }

    public void union(int u, int v) {
        int pu = find(u);
        int pv = find(v);
        if (pu == pv) {
            return;
        }
        if (rank[pu] < rank[pv]) {
            rank[pv]++;
            parent[pu] = pv;
        } else {
            rank[pu]++;
            parent[pv] = pu;
        }
    }

    public boolean isConnect(int u, int v) {
        return find(u) == find(v);
    }
}

class Solution {
    public int[] findRedundantDirectedConnection(int[][] edges) {
        int size = edges.length;
        int[] indegree = new int[size + 1];
        int has2Indegrees = -1;
        for (int[] e : edges) {
            indegree[e[1]]++;
            if (indegree[e[1]] == 2) {
                has2Indegrees = e[1];
                break;
            }
        }
        if (has2Indegrees == -1) {
            return detectCycle(edges, size, null);
        }
        for (int i = size - 1; i >= 0; --i) {
            if (edges[i][1] == has2Indegrees) {
                if (detectCycle(edges, size, edges[i]) == null) {
                    return edges[i];
                }
            }
        }
        return new int[2];
    }

    private int[] detectCycle(int[][] edges, int size, int[] skipEdge) {
        UnionFind uf = new UnionFind(size);
        for (int[] e : edges) {
            if (Arrays.equals(e, skipEdge)) {
                continue;
            }
            if (uf.isConnect(e[0], e[1])) {
                return e;
            } else {
                uf.union(e[0], e[1]);
            }
        }
        return null;
    }
}
