from collections import defaultdict

def build_graph(edges):
    adj = defaultdict(list); in_degree = defaultdict(int)
    for u, v in edges:
        adj[u].append(v)
        in_degree[v]+=1; in_degree[u]+=0

    return adj, in_degree

def find_redundant(edges):
    """Find all non tree edges in the directed graph"""
    adj, in_degree = build_graph(edges)

    visiting = set(); visited = set()
    parent = {}

    # collect back / forward / cross edges
    backedges = []; forward_cross = [] 

    def dfs(v, p):
        """
        v: vertex to be visit
        p: predecessor of v
        """
        if v in visiting:
            # graph has a cycle
            backedges.append([p, v])
            while p != v:
                backedges.append([parent[p], p])
                p = parent[p]
            return

        if v in visited:
            if p is None:
                return 
            # graph has forward or cross edges
            forward_cross.append([parent[v], v])
            forward_cross.append([p, v])
            return

        parent[v] = p
        visiting.add(v)
        if v in adj:
            for c in adj[v]:
                dfs(c, v)

        visiting.remove(v)
        visited.add(v)
        return 


    H = {tuple(e): i for i, e in enumerate(edges)}

    # check if there is a vertex with in-degree 0
    for s in in_degree:
        if in_degree[s] == 0:
            dfs(s, None)
            # if the graph has a cycle, remove the 
            # the first appearance of backedge
            if backedges:
                return backedges[0]    
            break
    else:
        for v in adj:
            dfs(v, None)

        if backedges:
            # remove the edge on the cycle that occurs last in the input
            return max(backedges, key=lambda x: H[x[0], x[1]])

    # if the graph has no cycle remove the last forward / cross edge
    return max(forward_cross, key=lambda x: H[x[0], x[1]])
