class Solution {
public:
    vector<int> findRedundantDirectedConnection(vector<vector<int>>& edges) {
        int m = edges.size();
        vector<int> root(m+1, 0), first, second;
        for (auto& edge : edges)
        {
            if (root[edge[1]] == 0)
            {
                root[edge[1]] = edge[0];
            }
            else
            {
                first = {root[edge[1]], edge[1]};
                second = edge;
                edge[1] = 0;
            }
        }

        for (int i = 0; i < root.size(); ++i)
        {
            root[i] = i;
        }

        for (auto& edge : edges)
        {
            int root_a = find(edge[0], root), root_b = find(edge[1], root);
            if (root_a == root_b)
            {
                if (!first.empty())
                {
                    return first;
                }
                else
                {
                    return edge;
                }
            }

            root[root_a] = root_b;
        }

        return second;  // Two situations here: 1) No circle due to the deletion of second: second is in the circle and it should be returned. 2) No circle at all. It is the second (last) edge that should be deleted.
    }

    int find(int node, vector<int>& root)
    {
        if (root[node] == node) return node;
        return find(root[node], root);
    }
};
