class Solution {
public:
    vector<int> res;
    bool flag;
    //path compression
    int find(int a,vector<int> &parent)
    {
        vector<int> v;
        while(parent[a]>0)
            v.push_back(a),a=parent[a];

        for(int i=0;i<v.size();++i)
            parent[v[i]]=a;
        return a;
    }
    //union by rank
    void Union(int A,int B,vector<int> &parent)
    {
        int a=find(A,parent);
        int b=find(B,parent);
        if(a==b)
        {
            res={A,B};
            flag=true;
            return;
        }
        int Ra=abs(parent[a]);
        int Rb=abs(parent[b]);
        if(Ra>Rb)
            parent[a]+=parent[b],parent[b]=a;
        else
            parent[b]+=parent[a],parent[a]=b;
        return;
    }


    vector<int> findRedundantDirectedConnection(vector<vector<int>>& edges) {

        vector<int> indegree(edges.size()+1,0);
        for(int i=0;i<edges.size();++i)
            ++indegree[edges[i][1]];

        vector<int> parent(edges.size()+1,-1);
        queue<pair<int,int>> q;
        for(int i=0;i<edges.size();++i)
        {
            int a,b;
            a=edges[i][0],b=edges[i][1];
            if(indegree[b]>1)
                q.push({a,b});
            else
                Union(a,b,parent);
        }
        while(q.size())
        {
            Union(q.front().first,q.front().second,parent);
            q.pop();
        }
        return res;
    }
};

