class Solution:
    def findRedundantDirectedConnection(self, edges: List[List[int]]) -> List[int]:
        parents = [0] * (len(edges) + 1)
        roots = [0] * (len(edges) + 1)
        ranks = [1] * (len(edges) + 1)
        ans1 = 0
        ans2 = 0

        def find(x):
            if roots[x] != x:
                x = find(roots[x])
            return x
        ans1 = None
        ans2 = None
        for edge in edges:
            u, v = edge
            if parents[v] == 0:
