class Solution {

    public int[] findRedundantDirectedConnection(int[][] edges) {
        int N = getNodes(edges);
        List<int[]> list = new ArrayList<>();
        List<Integer> graph[] = new ArrayList[N+1];

        for (int i = 1; i<= N; i++) {
            graph[i] = new ArrayList<>();
        }
        for (int[] num: edges) {
            graph[num[0]].add(num[1]);
        }

        boolean found = false;
        boolean[] visited = new boolean[N+1];
        boolean[] sptset = new boolean[N+1];        

        Map<Integer, Integer> map = new HashMap<>();
        List<int[]> parents = new ArrayList<>();
        // check for multiple parents first;
        int latest = -1; int last[] = new int[2];
        for (int num[]: edges) {
            if (map.containsKey(num[1])) {
                parents.add(new int[]{map.get(num[1]), num[1]});
                parents.add(num);
            }
            map.put(num[1], num[0]);
        }

        if (parents.size() > 0) {
            //check for last entry
            int[] temp = parents.get(1);
            List<Integer> tempList = graph[temp[0]];
            tempList.remove(Integer.valueOf(temp[1]));

            for (int node = 1; node <= N; node++) {
            if (cycle (graph, visited, -1, node, sptset, list)) 
                found = true;
            }

            if (found) return parents.get(0); else return parents.get(1);
            //graph[temp[0]].add(temp[1]);
        }

        for (int node = 1; node <= N; node++) {
            if (cycle (graph, visited, -1, node, sptset, list)) 
                found = true;
        }
        if (found) return list.get(0);

        return last;
    }

    public boolean cycle (List<Integer> graph[], boolean[] visited, int parent, int src, boolean[] sptset, List<int[]> list) {
        if (sptset[src]) {
            if (parent != -1) list.add(new int[]{parent, src});
            return true;  
        } 
        if (visited[src]) return false;

        sptset[src] = true;
        visited[src] = true;

        for (int child : graph[src]) {
            if (cycle(graph, visited, src, child, sptset, list))
                return true;
        }

        sptset[src] = false;
        return false;

    }

    public int getNodes (int[][] edges) {
        int max = 0;
        for (int[] num : edges) {
            max = Math.max(Math.max(num[0], num[1]), max);
        }
        return max;
    }
