class Solution(object):
    def findRedundantDirectedConnection(self, edges):
        indegree,candidates,parent = collections.defaultdict(list),[], {}
        for u,v in edges:
            indegree[v].append(u)
            if len(indegree[v]) == 2:
                candidates = [[uu,v] for uu in indegree[v]]
                indegree[v].pop()


        if candidates: edges.remove(candidates[1])
        for u,v in edges:
            if u not in parent: parent[u] = u
            if v not in parent: parent[v] = v
            pu,pv = self.gp(u,parent), self.gp(v,parent) 
            if pu == pv: 
				# PROBLEM DETECTED
				# if candidates is [] then its a cycle return [u,v]
				# else return the other one 
				return candidates[0] if candidates else [u,v]
            if pu!= pv: parent[pu] = pv
        return candidates[1]
    def gp(self,u,parent):
        if parent[u] != u: parent[u] = self.gp(parent[u],parent)
        return parent[u]
