class UnionFind{
        int[] f;
        public UnionFind(int size){
            f = new int[size];
            for(int i = 0; i < size; i++){
                f[i] = i;
            }
        }
        public int find(int x){
            int y = x;
            while(f[y] != y){
                y = f[y];
            }
            while(x != y){
                int fx = f[x];
                f[x] = y;
                x = fx;
            }
            return y;
        }
        public void union(int x, int y){
            int fx = find(x);
            int fy = find(y);
            if(fx != fy){
                f[fx] = fy;
            }
        }
    }
    //1.每个节点只有1个parent -> 删除形成环的那条边
    //2.1 有一个节点有2个parent，没有形成环，删除第二条边
    //2.2 有一个节点有2个parent，形成环，删除形成环且有两个parent的那条边
    public int[] findRedundantDirectedConnection(int[][] edges) {
        int n = edges.length;
        UnionFind uf = new UnionFind(n + 1);
        int[] parents = new int[n + 1];
        int[] ans1 = new int[2];
        int[] ans2 = new int[2];
        for(int i = 0; i < edges.length; i++){
            int u = edges[i][0];
            int v = edges[i][1];
            if(parents[v] > 0){
                ans1[0] = parents[v];
                ans1[1] = v;

                ans2[0] = u;
                ans2[1] = v;

                edges[i][0] = -1;
                edges[i][1] = -1;
            }

            parents[v] = u;
        }

        for(int i = 0; i < edges.length; i++){
            int u = edges[i][0];
            int v = edges[i][1];
            if(u == -1 && v == -1){
               continue; 
            }
            int fu = uf.find(u);
            int fv = uf.find(v);
            if(fu != fv){
                uf.union(fu, fv);
            }else{ //fu == fv
                if(ans1[0] != 0){
                    return ans1;
                }else{
                    return edges[i];
                }
            }
        }
        return ans2;
    }
