class Solution {
    Map<Integer,Integer> parent= new HashMap<>();
    Map<Integer,Integer> root= new HashMap<>();
    int[] ans1 = {-1,-1}, ans2={-1,-1};
    public int[] findRedundantDirectedConnection(int[][] edges) {
         for(int[] e: edges){
            int u= e[0],v=e[1];
            if(parent.containsKey(v)){
                ans2= new int[]{e[0], e[1]};
                ans1= new int[]{parent.get(v),v};
                e[0]=-1;e[1]=-1;
            }else parent.put(v,u);
        }

        for(int[] e: edges){
            int u= e[0],v=e[1];
            if(u<0 || v<0) continue;
            int p1= find(u),p2= find(v);
            if(p1==p2)
                return ans1[0]==-1? e: ans1;
            root.put(p1,p2);
        }
        return ans2;
    }
    int find(int x){
        root.putIfAbsent(x,x);
        while(x!=root.get(x)) x=root.get(x);
        return x;
    }
}
