class Solution {
public:
    vector<int> res;
    unordered_map<int, int> f;
    vector<int> findRedundantDirectedConnection(vector<vector<int>>& edges) {
        unordered_map<int, int> t;
        vector<vector<int>> cand;
        for(auto e:edges){
            if (!t.count(e[1])){
                t[e[1]]=e[0];
            }
            else{                
                cand.push_back(e);
                cand.push_back({t[e[1]],e[1]});                
            }
        }

        for(auto e:edges){
            if (!f.count(e[0])) f[e[0]] = e[0];
            if (!f.count(e[1])) f[e[1]] = e[1];
            if (!cand.size()){
                if(!uni(e[0], e[1])) return res;
            }
            else{
                if (!(e[0]==cand[0][0]&&e[1]==cand[0][1])){
                    if(!uni(e[0], e[1])) return cand[1];
                }
            }
        }

        return cand[0];
    }

    int find(int a){
        if (f[a]!=a){
            f[a]=find(f[a]);
        }
        return f[a];
    }

    bool uni(int a, int b){
        int af = find(a);
        int bf = find(b);
        if (af==bf){
            res = {a,b};
            return false;
        }        
        f[bf]=af;
        return true;        
    }
};
