class Solution {
    public int[] findRedundantDirectedConnection(int[][] edges) {
        int parent[] = new int[edges.length + 1];
        Arrays.fill(parent, -1);
        for(int[] edge: edges) {
            if(parent[edge[1]] != -1) { //this means that this edge has already a parent
                    int [] redundant1 = {edge[0], edge[1]};
                    if(isValidWithoutEdge(redundant1, edges)) {
                        return redundant1;
                    }

                    int [] redundant2 = {parent[edge[1]], edge[1]};
                    if(isValidWithoutEdge(redundant2, edges)) {
                        return redundant2;
                    }
            } else parent[edge[1]] = edge[0];

        }

        return findInvalidEdge(edges);
    }

    //if a child has 2 parents, than either one can be removed to make it a proper graph, its possible return that edge
    public boolean isValidWithoutEdge(int[] redundant, int[][] edges) {
        Disjoint set = new Disjoint();
        set.make(edges.length);

        for(int edge[]: edges) {
            if(edge[0] == redundant[0] && edge[1] == redundant[1]) continue;
            if(!set.unionPossible(edge[0], edge[1])) return false;
        }
        return true;
    }

    //if removing redundant edge does not work than return find cycle
    public int[] findInvalidEdge(int[][] edges) {
        Disjoint set = new Disjoint();
        set.make(edges.length);

        for(int edge[]: edges) {
            if(!set.unionPossible(edge[0], edge[1])) return edge;
        }
        return edges[edges.length - 1];
    }

}

class Disjoint {
    int parent[]; 
    int rank[];
    public void make(int n) {
        parent = new int[n + 1];
        rank = new int[n+1];
        for(int i = 0; i<parent.length; i++) parent[i] = i;
    }

    public int find(int i) {
        if(parent[i] == i) return i;
        else return find(parent[i]);       
    }

    //UNION by rank for efficiency
    public boolean unionPossible(int i, int j) {
         int p1 = find(i);
         int p2 = find(j);
         if(p1 != p2) {
             if(rank[p1] >= p2) {
                 parent[p2] = p1;
                 rank[p1]++;
             } else {
                 parent[p1] = p2;
                 rank[p2]++;
             }
            return true;
         } else return false;        
    }
}
