class Solution {
public:
    vector<int> findRedundantDirectedConnection(vector<vector<int>>& edges) {
        unordered_map<int,vector<int>> parents;
        int flag = -1;
        for(auto edge:edges){
            if(edge[0]==edge[1]) return edge;
            parents[edge[1]].push_back(edge[0]);
            if(parents[edge[1]].size()==2) flag = edge[1];
        }
        if(flag==-1){

            for(int i=(int)edges.size()-1;i>=0;--i){
                unordered_set<int> has_visited;
                int from = edges[i][0],to = edges[i][1];
                int p = from;

                while(parents.count(p)&&has_visited.count(p)==0){
                    has_visited.insert(p);
                    p = parents[p][0];
                    if(p==to) return edges[i];
                }
            }
            //return vector<int>({-1,-1});
        }
        else{
            int to = flag, from = parents[flag][1];
            int p = from;
            while(parents.count(p)){
                p = parents[p][0];
                if(p==flag) return vector<int>({from,to});
            }

            from = parents[flag][0];
            p = from;
            while(parents.count(p)){
                p = parents[p][0];
                if(p==flag) return vector<int>({from,to});
            }
            return vector<int>({parents[flag][1],flag});
        }
    }
};
