class Solution {
    private int[] father;
    public int[] findRedundantDirectedConnection(int[][] edges) {
        /* Tree with n nodes has exactly n-1 edges(every node possess exactly one parent node except the root node)
            Here we have one additional Edges Added, 
            so edges.lenth == n
        */
        int n = edges.length;

        father = new int[n+1];
        /* Union & Find With Optimization */
        // 1. Locate the Node with Two Direct Parents
        for(int[] E : edges){
            if(father[E[1]] != 0){
                int[] edge1 = new int[]{father[E[1]], E[1]};
                int[] edge2 = E;
                // Later First
                if(ifTreeWithOutAnEdge(edges, edge2)){
                    return edge2;
                }
                if(ifTreeWithOutAnEdge(edges, edge1)){
                    return edge1;
                }
            }
            else father[E[1]] = E[0];
        }

        // 2. Extra Edge is Target at Root Node, Then There is Root Within, Found The Reduandancy Edge

        for(int i=0; i<=edges.length; i++)father[i] = i;

        for(int i=0; i<edges.length; i++){
            int u = edges[i][0];
            int v = edges[i][1];
            if(!union(u, v))return edges[i];
        }
        return null;
    }

    private boolean ifTreeWithOutAnEdge(int[][] edges, int[] edge){
        // Initialization
        for(int i=0; i<=edges.length; i++)father[i] = i;

        for(int i=0; i<edges.length; i++){
            if(edges[i][0] == edge[0] && edges[i][1] == edge[1])continue;
            int u = edges[i][0];
            int v = edges[i][1];
            if(!union(u, v))return false;
        }

        return true;
    }

    /* Approach: Union & Find */
    public int find(int cur){
        // No Path Compression
        if(father[cur] == cur)return cur;
        return find(father[cur]);
    }

    /* Union with Order, (u, v) => u -> v, v's father is u */
    public boolean union(int u, int v){
        int uP = find(u);
        int vP = find(v);

        if(vP != v)return false; // A Node Cann't Have Multiple Direct Father
        if(uP == vP)return false; // Redundancy Found
        else{
            father[vP] = uP;
            return true;
        }
    }
}
