class DisjointSet{
    vector<int> rank, size, parent;

    public:
        DisjointSet(int n){
            rank.resize(n+1, 0);
            size.resize(n+1);
            parent.resize(n+1);
            for(int i = 0; i <= n; i++){
                parent[i] = i;
                size[i] = 1;
            }
        }

        int findParent(int node){
            if(node == parent[node]){
                return node;
            }

            return parent[node] = findParent(parent[node]);
        }

        void unionByRank(int u, int v){
            int ulpU = findParent(u);
            int ulpV = findParent(v);

            // same component
            if(ulpU == ulpV)
                return ;

            // otherwise
            if(rank[ulpU] < rank[ulpV]){
                parent[ulpU] = ulpV;
            } else if(rank[ulpV] < rank[ulpU]){
                parent[ulpV] = ulpU;
            } else {
                parent[ulpV] = ulpU;
                rank[ulpU]++;
            }
        }

        void unionBySize(int u, int v){
            int ulpU = findParent(u);
            int ulpV = findParent(v);

            // same component
            if(ulpU == ulpV)
                return ;

            // otherwise
            if(size[ulpU] < size[ulpV]){
                parent[ulpU] = ulpV;
                size[ulpV] += size[ulpU];
            } else {
                parent[ulpV] = ulpU;
                size[ulpU] += size[ulpV];
            }
        }
};

class Solution {
public:
    vector<int> findRedundantDirectedConnection(vector<vector<int>>& edges) {
        int n = edges.size();

        // First get the two edges that are having two indegree
        vector<int> indegree(n+1, -1); // because of 1 based indexing
        int blacklist1 = -1;
        int blacklist2 = -2;

        for(int i = 0; i < n; i++){
            int u = edges[i][0];
            int v = edges[i][1];

            if(indegree[v] != -1){
                blacklist1 = i;
                blacklist2 = indegree[v];
                break;
            } else {
                indegree[v] = i;
            }
        }


        <!-- Create Disjoint set for cycle detection. -->
        DisjointSet ds(n+1);
        bool isCyclePresent = false;
        for(int i = 0; i < n; i++){
            <!-- Ignoring blacklist 1 edge -->
            if(i == blacklist1){
                continue;
            }

            int u = edges[i][0];
            int v = edges[i][1];

            <!-- If parent different then add edge to graph -->
            if(ds.findParent(u) != ds.findParent(v)){
                ds.unionBySize(u, v);
            } else { // If parent same, we are in cycle and then process our answer
                isCyclePresent = true;
                if(isCyclePresent){
                    if(blacklist1 == -1)
                        return edges[i];
                    else
                        return edges[blacklist2];
                }
            }
        }

        return edges[blacklist1];
    }
};
