        int pa = findParent(a, p);
        int pb = findParent(b, p);
        if(pa == pb){return false;}
        p[pa] = pb;
        return true;

    }

    public boolean dfs(int root, int[][]edges, int n,  int[]e2){
        List<Integer>[] adj= new ArrayList[n];


        for(int i = 0; i < n; i++){
            adj[i] = new ArrayList<>();
        }
        for(int[] e : edges){
            if(e != e2){
                adj[e[0] - 1].add(e[1] - 1);
            }
        }


        boolean visited[] = new boolean[n];
        visited[root] = true;
        Queue<Integer> q = new LinkedList<>();
        q.add(root);
        while(!q.isEmpty()){
            int ver = q.poll();
            for(int x: adj[ver]){
                if(!visited[x]){
                    visited[x] = true;
                    q.add(x);
                }
            }
        }



        for(int i = 0; i < n; i++){
            if(!visited[i]){
                return false;
            }

        }
        return true;

    }
    public int[] findRedundantDirectedConnection(int[][] edges) {
        //root -> search for cycle start of the cycle 
        // search for node with 2 parents
        //find mother vertex
            //1.
            //find the vertex with no parents 
            //progressively go through the edges and find the edge whose addition makes the repeated vertex
            //2. 
            //if no vertex with no parent 
            //we will just get the add till you get a cycle 

            int n = edges.length;
            boolean vis[] = new boolean[n];

            for(int e[] : edges)
            {
                vis[e[1] - 1] = true;
            }

            boolean rootPresent = false;
            int root = -1;
            for(int i = 0; i < n ; i++){
                if(!vis[i]){
                    rootPresent = true;
                    root = i;
                    break;
                }
            }


            if(rootPresent){
                int count[] = new int[n];
                int culPritVer = 0;
                int e1[] = null, e2[] = null ;
                for(int e[] : edges){
                    if(++count[e[1] - 1] > 1){
                        culPritVer = e[1] - 1;
                        e2 = e;
                        break;
                    }
                }


                for(int e[] : edges){
                    if(e[1] - 1 == culPritVer){
                        e1 = e;
                        break;
                    }
                }


                boolean visi[] = new boolean[n];
                int cnt = n;
                // System.out.println("e1: "  + Arrays.toString(e1));
                // System.out.println("e2: "  + Arrays.toString(e2));
                for(int e[] : edges){
                    if(e != e2){
                        // System.out.println(Arrays.toString(e));
                        if(!visi[e[0] - 1] && (e[0] - 1) == root){
                            visi[e[0] - 1] = true;
                            cnt--;
                        }
                        if(!visi[e[1] - 1]){
                            visi[e[1] - 1] = true;
                            cnt--;
                        }
                    }
                }


                if(cnt == 0 && dfs(root, edges,n, e2)) {
                    return e2;
                }
                else{
                    return e1;
                }

            }
            else{
                int parent[] = new int[n];
                for(int i = 0; i < n; i++){
                    parent[i] = i;
                }
                for(int e[] : edges){
                    if(!union(e[0] - 1, e[1] - 1, parent)){
                        return e;
                    }
                }


            }

            return null;
    }
}
