class Solution {
private: 
    vector<int> rank, par;
    int n;
public:
        // if no in_deg == 2 node (cycle - all nodes with in_deg == 0 or in_deg == 1)
        // return the first occurence of union b/w nodes with same parent
        // if an in_deg == 2 node exists, remove the edge (of the 2 incoming edges) that doesn't break the tree
        // first union all nodes except the ones that contribute to indgree == 2 node, then of the remaining two, return the ans according to the node/edge that that makes it a tree

    vector<int> findRedundantDirectedConnection(vector<vector<int>>& edges) {
        n = edges.size();
        rank = vector<int>(n + 1,0);
        par = vector<int>(n + 1);
        for (int i = 0; i <= n; i++) par[i] = i;

        // find if a deg == 2 node exists
        int in2dnode = -1;
        vector<int> in = vector<int>(n + 1, 0);
        for (const vector<int> &edge: edges) {
            in[edge[1]]++;
            if (in[edge[1]] == 2) {
                in2dnode = edge[1];
                break;
            }
        }

        vector<int> in_start; // max size = 2 (will contain both nodes that contribute to 2 deg node)
        for (const vector<int> &edge: edges) {
            int l = edge[0], r = edge[1];
            if (in2dnode == -1) {
                if (merge(l, r)) return {l, r};
            } else {
                if (edge[1] != in2dnode)
                    union_(l, r);
                else in_start.push_back(edge[0]);
            }
        }
        return {merge(in_start[0], in2dnode) ? in_start[0] : in_start[1] , in2dnode};
    }

    bool merge(int l, int r) {
        bool res = false;
        if (parent(l) == parent(r)) {
            res = true;
        } else {
            union_(l, r);
        }
        return res;
    }

    int parent(int x) {
        while (x != par[x]) {
            par[x] = par[par[x]];
            x = par[x];
        }
        return x;
    }

    void union_(int x, int y) {
        x = parent(x), y = parent(y);
        if (x != y) {
            if (rank[x] < rank[y]) swap(x, y);
            par[y] = x;
            rank[x] += rank[x] == rank[y];
        }
    }
};
