class Solution {
public:
    // pepcoding 
    // https://www.youtube.com/watch?v=d0tqBMRZ6UQ
    int find(int a , vector<int> & parent) {
        if(a == parent[a]){
            return a;
        }
        return parent[a] = find(parent[a],parent);
    }
    void union_(int a, int b,vector<int> &rank,vector<int>& parent){
        if(rank[a] > rank[b]){
            parent[b] = a;
        }else if(rank[a] < rank[b]){
            parent[a] = b;
        }else{
            parent[b] = a;
            rank[a]++;
        }
    }
    vector<int> findRedundantDirectedConnection(vector<vector<int>>& edges) {
        vector<int> ans;
        int n{};
        for(auto &v: edges) {
            n = max({n,v[0],v[1]});
        }

        int en = edges.size();
        int potEdge1{-1}; // potential Extra Edge
        int potEdge2{-1};
        vector<int>inDeg(n+1,-1); // -1 false ,other number tells which edge made indeg
        for(int i{}; i < en; i++) {
            if(inDeg[edges[i][1]] != -1) {
                potEdge1 = inDeg[edges[i][1]];
                potEdge2 = i;
                break;
            }
            inDeg[edges[i][1]] = i;
        }

        vector<int> parent(n+1);
        for(int i{}; i <= n; i++) {
            parent[i] = i;
        }
        vector<int>rank(n+1,1);

        vector<int> cycleEdge = {-1,-1};
        for(int i{}; i < en; i++) {

            // if we found the node with inDegree 2 it will exclude 
            // the later edge and then try to find the cycle 
            // otherwise potEdge2 will be -1 and(hence) it will consider all edges
            if(i == potEdge2) {continue;} 

            int sp = find(edges[i][0],parent);
            int dp = find(edges[i][1],parent);
            if(sp == dp){
                cycleEdge = edges[i];
            }else{
                // merging is done as undirected graph 
                // because there's non property of  
                // directed graph that we are using here
                union_(sp,dp,rank,parent);
            }
        }

        if(potEdge1 == -1){
            // no node with 2 inDegree -> Case 2
            return cycleEdge;
        }else if(cycleEdge[0] == -1){
            // node with 2 inDegree without cycle -> Case 1
            return edges[potEdge2];
        }else{
            // node with 2 inDegree with cycle -> Case 3
            return edges[potEdge1];
        }
    }
};
