class Solution {
    vector<int> parent ;
    vector<int> rank ;
    void memoryset(){
        for(int i=1;i<parent.size();i++){
            parent[i] = i ;
            rank[i]=0;
        }
    }

    int findParent(int node){
        if(node == parent[node]) return node ;

        return parent[node] = findParent(parent[node]);
    }

    void unionSet(int a , int b){
        a = findParent(a);
        b = findParent(b);

        if(a==b) return ;

        if(rank[a]>rank[b]){
            parent[b] = a; 
        }else if(rank[a]<rank[b]){
            parent[a] = b ;
        }else {
            parent[a] = b ;
            rank[b]++;
        }
        return ;
    }

public:
    vector<int> findRedundantDirectedConnection(vector<vector<int>>& e) {
        int n = e.size();
        parent.resize(n+1);
        rank.resize(n+1);

        memoryset();

        vector<int> ind(n+1,-1);
        int bl1 = -1 , bl2 = -1 ;

        for(int i=0;i<n;i++){
            int v = e[i][1] ;

            if(ind[v] != -1){
                bl1 = i;
                bl2 = ind[v];
                break ;
            }else{
                ind[v] = i ;
            }
        }

        for(int i=0;i<n;i++){
            if(i==bl1) continue ;

            int u = findParent(e[i][0]);
            int v = findParent(e[i][1]);

            if(u==v){
                if(bl1 == -1){
                    return e[i]; // when there is only cycle and each node has only one parent
                }else{
                    return e[bl2]; // when we remove current edge but there is still cycle 
                }
            }else{
                unionSet(u,v);
            }
        }
        return e[bl1]; 
    }
};
