        int n = edges.size(); // #edges = n-1+1
        vector<int> parent(n+1);
        vector<int> rank(n+1);
        makeset(parent, rank);

        vector<int> indegree(n+1, -1);
        vector<int> blacklist(2); // containing index of edges that are contender of ans i.e. has 2 parents. 
        blacklist[0] = -1;
        blacklist[1] = -1;
        for(int i = 0 ; i < edges.size() ; i++)
        {
            if(indegree[edges[i][1]] == -1)
            {
                indegree[edges[i][1]] = i; 
