            else
            {
                blacklist[0] = indegree[edges[i][1]];
                blacklist[1] = i;
                break;
            }
        }
        vector<int> ans;
        if(blacklist[1] != -1)
            ans = edges[blacklist[1]];

        for(int i = 0 ; i < edges.size() ; i++)
        {
            if(i != blacklist[1])
            {
                if(findparent(edges[i][1], parent) != findparent(edges[i][0], parent))
                {
                    Union(edges[i][0], edges[i][1], parent, rank);
                }
                else
                {
                    if(blacklist[0] == -1)
                        ans = edges[i];
                    else
                        ans = edges[blacklist[0]];
                }
            }
        }
