class Solution {
public:

    int find(int x,vector<int>& par) {
        if(x==par[x])
            return x;
        int y=find(par[x],par);
        return par[x]=y;
    }

    bool merge(int x,int y,vector<int>& par,vector<int>& rank) {
        int lx=find(x,par);
        int ly=find(y,par);
        if(lx!=ly)
        {
            if(rank[lx]>rank[ly])
            par[ly]=lx;
            else if(rank[ly]>rank[lx])
            par[lx]=ly;
            else
            {
                par[ly]=lx;
                rank[lx]++;
            }
            return 0;
        }
        return 1;
    }

    vector<int> findRedundantDirectedConnection(vector<vector<int>>& edges) {
        int n=edges.size();
        vector<int> par(n+1),rank(n+1),indeg(n+1,-1);
        for(int i=1;i<=n;i++)
        {
            par[i]=i;
            rank[i]=0;
        }
        int e1=-1,e2=-1;
