class Solution {
public:

    int find(int x,vector<int>& par) {
        if(x==par[x])
            return x;
        int y=find(par[x],par);
        return par[x]=y;
    }

    bool merge(int x,int y,vector<int>& par,vector<int>& rank) {
        int lx=find(x,par);
        int ly=find(y,par);
        if(lx!=ly)
        {
            if(rank[lx]>rank[ly])
            par[ly]=lx;
            else if(rank[ly]>rank[lx])
            par[lx]=ly;
            else
            {
                par[ly]=lx;
                rank[lx]++;
            }
            return 0;
        }
        return 1;
    }

