from collections import defaultdict

class UF:
    def __init__(self, n):
        self.parent = [i for i in range(n + 1)]

    def union(self, x, y):
        px, py = self.find(x), self.find(y)
        if px != py:
            self.parent[px] = py
            return 0
        return 1

    def find(self, x):
        if self.parent[x] != x:
            self.parent[x] = self.find(self.parent[x])
        return self.parent[x]

class Solution:
    def findRedundantDirectedConnection(self, edges: List[List[int]]) -> List[int]:
        n = len(edges)
        uf = UF(n)
        in_edge = defaultdict(list)
        candidate_1, candidate_2 = None, None

        for x, y in edges:
            if len(in_edge[y]) == 0:
                in_edge[y].append([x, y])
            else:
                candidate_1 = [x, y]
                candidate_2 = in_edge[y][0]
        for x, y in edges:
            if [x, y] == candidate_1:
                continue
            if uf.union(x, y):
                if not candidate_1:
                    return [x, y]
                else:
                    return candidate_2
        return candidate_1
