class DisjointSet{

    public:
    vector<int> rank,parent,size;
     DisjointSet(int n){
         rank.resize(n+1,0);
         parent.resize(n+1);
         size.resize(n+1,1);
         for(int i=0;i<n;i++){
             parent[i]=i;
         }
     }
     int findUPar(int node){
         if(node==parent[node]) return node;
         return parent[node]=findUPar(parent[node]);
     }
     void unionByRank(int u,int v){
         int ulp_u = findUPar(u);
         int ulp_v = findUPar(v);
         if(ulp_u == ulp_v) return;
         if(rank[ulp_u]<rank[ulp_v]){
             parent[ulp_u]=ulp_v;
         }
         else if(rank[ulp_u]>rank[ulp_v]){
             parent[ulp_v]=ulp_u;
         }
         else{
             parent[ulp_v] = ulp_u;
            rank[ulp_u]++;
         }
     }
     void unionBySize(int u,int v){
         int ulp_u = findUPar(u);
         int ulp_v = findUPar(v);
         if(ulp_u == ulp_v) return;
         if(size[ulp_u]<size[ulp_v]){
             parent[ulp_u]=ulp_v;
             size[ulp_v]++;
         }

         else{
             parent[ulp_v] = ulp_u;
            size[ulp_u]++;
         }
     }
};

class Solution {
public:
    vector<int> findRedundantDirectedConnection(vector<vector<int>>& edges) {
        int n = edges.size();

      vector<int> parnode(n+1,0),dout1,dout2;
      for(auto &edge : edges){
          if(parnode[edge[1]]==0){
              parnode[edge[1]]=edge[0];
          }else{
              dout1 = {parnode[edge[1]],edge[1]};
              dout2 = edge;
              edge[1]=0;
          }
      }
          DisjointSet ds1(n+1);
          for(auto &edge: edges){
              if(edge == dout2) continue;
              int u = edge[0];
              int v = edge[1];
              if(ds1.findUPar(u)==ds1.findUPar(v)){
                  if(dout1.empty()) return edge;
                  return dout1;
              }
                  ds1.unionBySize(u,v);
          }

      return dout2;
    }
};
