   	// ALTERNATIVE
   	// Alternative was Linear Search but usme time jyada lagta
   	// Usme bhi 1 to x ham loop chalate aur i ko sqare karke dekhte ki kahin wo eqaul hain ya bada hain x se.
   	// In that we can use the concept of Power Exponentiation.

    //TC - O(log(x)) Binary Search
    //SC - O(1)
    public:
        int Exponential(int m, double mid)
        {
            long long ans = 1;
            for (int i = 1; i <= 2; i++)
            {
                ans = ans * mid;
                if (ans > m)
                   	//Ans m se bada hua matlab high mid-1 hoga
                    return 2;
            }
            if (ans == m)
               	//Barabar mil gaya
                return 0;
            else
               	//Low mid+1 hoga
                return 1;
        }
    int mySqrt(int x)
    {
        long long low = 1;
        long long high = x;
        long long mid;
        while (low <= high)
        {
            mid = (low + high) / 2;
            int expoResult = Exponential(x, mid);	//Comparison result
            if (expoResult == 0)	//equal
                return mid;
            else if (expoResult == 1)	//ans < x
                low = mid + 1;
            else
                high = mid - 1;	//ans>x
        }
        return high;
    }
};
