public int numTrees(int n) {
    // base case
    if (n == 0) return 0;

    // dp(i) represents the no. of unique BSTs till i
    // dp(0) = 1, means there's a null node
    int[] dp = new int[n + 1]; dp[0] = 1;

    for (int i = 1; i <= n; i++)
        // use j as root
        for (int j = 1; j <= i; j++)
            dp[i] += dp[j - 1] * dp[i - j];

    return dp[n];
}

