class Solution {
public:
    int maxRotateFunction(vector<int>& nums) {
        if(nums.size() == 1) return 0;
        int n = nums.size();
        int sum = 0;
        int rotSum = 0;
        for(int i = 0; i < n; i++){
            sum += nums[i];
            rotSum += i * nums[i];
        }
        int result = rotSum;
        for(int i = 1; i < n; i++){
            rotSum += sum - n * nums[n-i];
            result = max(result, rotSum);
        }

        return result;
    }
};
