        int l = A.length;
        for(int i=0;i<l;i++){
            Fk+=i*A[i];
            sum+=A[i];
        }
        int max=Fk;
        for(int k=1;k<l;k++){
            Fk=Fk-(l-1)*A[l-k] + (sum-A[l-k]);
            max=Math.max(max,Fk);
        }
        return max;
    }
}


