class Solution {
public:
    int maxRotateFunction(vector<int>& nums) {
        int n=nums.size();
        if(n == 1)
            return 0;

        int sum = accumulate(nums.begin(), nums.end(), 0);
        int val = 0;
        for(int i=0; i < n; i++)
            val += (i * nums[i]);

        int ans=val;
		/* For example nums = [4,3,2,6]
		n=4
		Initial value : val = 0 * 4 + 1 * 3 + 2 * 2 + 3 * 6 = 25 
		Next iteration : val = 0 * 6 + 1 * 4 + 2 *  3 + 3 * 2 = 16
		16 = (0 + 1) * 4 + (1 + 1) * 3 + (2 + 1) * 2 + (3 + 1) mod n  * 6 
		16 = 0 * 4 + 1 * 3 + 2 * 2 + 3 * 6 - 3 * 6 + 4 + 3 + 2
		16 = (0 * 4 + 1 * 3 + 2 * 2 + 3 * 6) + 4 + 3 + 2 - 3 * 6
		16 = (0 * 4 + 1 * 3 + 2 * 2 + 3 * 6) + 4 + 3 + 2 + 6 - 6 - 3 * 6
		16 = (0 * 4 + 1 * 3 + 2 * 2 + 3 * 6) + 4 + 3 + 2 + 6  - 4 *  6
		16 = previous value + sum - n * nums[n - i]
		...
		etc
		*/

        for(int i=1; i < n; i++) {
            val = (val + sum - nums[n - i] * n);
            ans = max(ans, val);
        }

        return ans;

    }
};
