class Solution {
 public int maxRotateFunction(int[] nums) {
        int ans=0;
        int sum=0;
     int finalAns=0;
     //initialize with the result of 1st iteration
        for(int i=0;i<nums.length;i++){
          ans+=i*nums[i];
          sum+=nums[i];
        }
        finalAns=ans;
        //result of nth iteration is calculated with the help of result found from n-1th iteration in the ans variable
        for(int i=1;i<nums.length;i++){
        	//ans is the result from previous iteration 
        	int currentAns=ans+sum-(nums.length*nums[nums.length-i]);
        	finalAns=Math.max(currentAns, finalAns);
            ans=currentAns;//store this result and use in n+1th iteration
        }


        return finalAns;
    }
}
