class Solution {
    public int maxRotateFunction(int[] arr) {
         int n = arr.length;
         int csum = 0; //current sum
         int asum = 0; //array sum
      for(int i = 0;i < arr.length;i++){
          csum += (i * arr[i]); 
          asum += arr[i];
      }
      //initialize max as first configuration max (i.e zero rotation)
      int max = csum;
      for(int i = 0;i<n - 1;i++){
           //new current sum (having i + 1 rotations)
           int ncsum = csum + asum -  n * arr[n - i - 1];
           max = Math.max(ncsum,max);
           csum = ncsum;
      }

      return max;
    }
}
