vector<int> nextGreaterElement(vector<int>& nums1, vector<int>& nums2) {
    vector<int> final;  //Final array that needs to be returned .
    unordered_map<int,int> m1;   //Map to hold the next greater elements for        each element.
    if(nums2.size()==0)
    return final;

    //A normal next greater element to the right approach using stack and populating map with the NGE for each element in nums2.
    stack<int> s;
    int n=nums2.size();
    s.push(nums2[n-1]); //For last element we dont have Next greater element
    m1[nums2[n-1]]=-1;
    for(int i=n-2;i>=0;i--){
        while(!s.empty() && s.top()<nums2[i])
            s.pop();
        if(!s.empty())
            m1[nums2[i]]=s.top();
        else
            m1[nums2[i]]=-1;
        s.push(nums2[i]);
    }

    //Fill the final array that needs to be returned with the next greater elements that we found for each element in the second array.
    for(int i=0;i<nums1.size();i++)
    final.push_back(m1[nums1[i]]);

    return final;
}

