    public static int findNextMaxNum(int[] arr , int n )
    {
         int temp = -1 ;
        for (int i = 0 ; i < arr.length ; i++)
        {
            if (arr[i] == n) {
                while ((i + 1) < arr.length && temp <= n) {
                    i++;
                    temp = Math.max(n, arr[i]);
