class Solution {
public:
    vector<int> nextGreaterElement(vector<int>& nums1, vector<int>& nums2) {
        int n = nums2.size();
        vector <int> ans;
        stack <int> s;
        s.push(nums2[n - 1]);
        unordered_map <int, int> mp;
        mp[nums2[n - 1]] = -1;
        for (int i = n - 2; i >= 0; i--) {
            while (s.empty() == false && s.top() <= nums2[i])
                s.pop();
            int nextGE = s.empty() ? -1 : s.top();
            mp[nums2[i]] = nextGE;
            s.push(nums2[i]);
        }
        for (int i = 0; i < nums1.size(); i++) 
            ans.push_back(mp[nums1[i]]);
        return ans; 
    }
};
