            while ((st.top() != -1) && (st.top() <= n2[i]))
                st.pop();
            int top = st.top();
            st.push(n2[i]);
            n2[i] = top;
        }
        for (int i = 0; i < n1.size(); i++)  n1[i] = n2[n1[i]];
        return n1;
    }
};
