    vector<int> nextGreaterElement(vector<int>& nums1, vector<int>& nums2) {
        vector<int>h;
        int flag=0;
        for(int i=0;i<nums1.size();i++)
        {
			//searching element in vector nums2

            int x=find(nums2.begin(),nums2.end(),nums1[i])-nums2.begin();

