class Solution {
    public int[] nextGreaterElement(int[] nums1, int[] nums2) {
        //to store results
        HashMap<Integer,Integer> hm=new HashMap<Integer,Integer>();

        //stack will help us achieve the next greater element
        Stack<Integer> stk=new Stack<Integer>();

        for(int i=nums2.length-1;i>=0;i--){
            if(stk.isEmpty()){  //no greater element is there after this element
                hm.put(nums2[i],-1);
                stk.push(nums2[i]);
            }else{
                //if top of stack is not greater than current element then we should remove it as it is and will not be of any use.
                while(!stk.isEmpty() && stk.peek()<=nums2[i])
                    stk.pop();

                //doing so if stk becomes empty 
                if(stk.isEmpty())
                    hm.put(nums2[i],-1);
                else    //else there is a greater element than this element on right
                    hm.put(nums2[i],stk.peek());
                stk.push(nums2[i]);
            }
        }

        int op[]=new int[nums1.length];
        for(int i=0;i<nums1.length;i++){
            op[i]=hm.get(nums1[i]);
        }
        return op;
    }
}
