class Solution {
    func nextGreaterElement(_ nums1: [Int], _ nums2: [Int]) -> [Int] {
        var dict = [Int: Int]() // [(num) : (next greater num)]
        var stack = [Int]()
        for num in nums2 {
            while !stack.isEmpty && stack.last! < num {
                dict[stack.removeLast()] = num
            }
            stack.append(num)
        }
        return nums1.map { dict[$0] ?? -1 }
    }
}
